document.querySelectorAll("#privacy-glass-wrapper .privacy-nav-btn").forEach(t=>{t.addEventListener("click",function(t){const e=this.getAttribute("href");if(e.startsWith("#")){t.preventDefault();const r=document.querySelector(e);r&&r.scrollIntoView({behavior:"smooth",block:"start"})}})}),document.querySelectorAll("#privacy-glass-wrapper .privacy-nav-btn, #privacy-glass-wrapper .privacy-cta-btn").forEach(t=>{t.addEventListener("pointerdown",function(){this.style.transform="scale(0.96)"}),t.addEventListener("pointerup",function(){this.style.transform=""}),t.addEventListener("pointerleave",function(){this.style.transform=""})});